#include <stdint.h>
#include <stdio.h>
#include "main.h"
#include "stack.h"
#include "faults.h"
#include "led.h"
#include "systick.h"

#if !defined(__SOFT_FP__) && defined(__ARM_FP)
  #warning "FPU is not initialized, but the project is compiling for an FPU. Please initialize the FPU before use."
#endif

//Changed to 1 since adding idle task to first elemtn is user_tasks array
uint8_t current_task = 1; //task1 is running
uint32_t g_tick_count = 0;


void task1_handler(void);
void task2_handler(void);
void task3_handler(void);
void task4_handler(void);
void idle_task();


void init_systick_timer(uint32_t tick_hz);
void switch_sp_to_psp(void);

int main(void)
{
    //print_stack_info();

    enable_processor_faults(1, 1, 1);

    init_scheduler_stack(SCHED_STACK_START);

    init_task_stack();

    led_init_all();

    init_systick_timer(TICK_HZ);

    switch_sp_to_psp();

    task1_handler();
    /* Loop forever */
	for(;;);
}

void task1_handler(void)
{
    while(1)
    {
        led_on(LED_GREEN);
        task_delay(1000);
        led_off(LED_GREEN);
        task_delay(1000);
    }
}

void task2_handler(void)
{
    while(1)
    {
        led_on(LED_ORANGE);
        task_delay(500);
        led_off(LED_ORANGE);
        task_delay(500);
    }
}

void task3_handler(void)
{
    while(1)
    {
        led_on(LED_BLUE);
        task_delay(250);
        led_off(LED_BLUE);
        task_delay(250);
    }
}

void task4_handler(void)
{
    while(1)
    {
        led_on(LED_RED);
        task_delay(150);
        led_off(LED_RED);
        task_delay(150);
    }
}

void idle_task()
{
    while(1);
}
